func prettifyText(fullString: NSString, boldPartsOfString: Array<NSString>, font: UIFont!, boldFont: UIFont!, paths: [String], linkedSubstrings: [String], accented: [String]) -> NSAttributedString {

    let nonBoldFontAttribute: [NSAttributedString.Key : Any] = [NSAttributedString.Key.font:font!, NSAttributedString.Key.foregroundColor: UIColor.label]
    let boldFontAttribute = [NSAttributedString.Key.font:boldFont!]
    let accentedAttribute:  [NSAttributedString.Key : Any] = [NSAttributedString.Key.foregroundColor: accentOrange, NSAttributedString.Key.strokeWidth: -5]
    
    let boldString = NSMutableAttributedString(string: fullString as String, attributes:nonBoldFontAttribute)
    for i in 0 ..< boldPartsOfString.count {
        boldString.addAttributes(boldFontAttribute, range: fullString.range(of: boldPartsOfString[i] as String))
    }
    for l in 0..<paths.count {
        let sbstrRange = fullString.range(of: linkedSubstrings[l])
        boldString.addAttribute(.link, value: paths[l], range: sbstrRange)
    }
    for a in 0..<accented.count {
        let sbstrRange = fullString.range(of: accented[a])
        
        boldString.addAttributes(accentedAttribute, range: sbstrRange)
    }
    return boldString
}

func topicStrToDict(str: String) {

    for i in 0..<(str.count / 4) {
        
        let keyStartIdx = str.index(str.startIndex, offsetBy: 4 * i)
        let keyEndIndx = str.index(str.startIndex, offsetBy: 4 * i + 2)
        let valStartIdx = str.index(str.startIndex, offsetBy: 4 * i + 2)
        let valEndIdx = str.index(str.startIndex, offsetBy: 4 * i + 4)
        
        let keyRange = keyStartIdx..<keyEndIndx
        let valRange = valStartIdx..<valEndIdx
                
        UserDefaults.setSliderValue(value: (str[valRange] as NSString).floatValue, slider: String(str[keyRange]))
    }
    
}

